var preform = document.querySelector(".form-title"), form = document.querySelector(".form-index"), dateon = document.querySelector("[name=arrival]"), dateoff = document.querySelector("[name=departure]"), link = document.querySelector(".searching");
link.addEventListener("click", function(a) {
  a.preventDefault();
});
preform.addEventListener("click", function(a) {
  form.classList.contains("animated-form") ? form.classList.remove("animated-form") : form.classList.add("animated-form");
});
form.addEventListener("submit", function(a) {
  dateon.value && dateoff.value || (a.preventDefault(), form.classList.add("form-error"));
});